JOIN `siswa` `s` ON `h`.`siswa_id` = `s`.`id_siswa`
JOIN `siswa_kelas_ajaran` `sk` ON `s`.`id_siswa` = `sk`.`siswa_id` AND `u`.`id_tahun_ajaran` = `sk`.`id_tahun_ajaran`
JOIN `kelas` `k` ON `sk`.`kelas_id` = `k`.`id_kelas`
LEFT JOIN `jenjang` `j` ON `k`.`id_jenjang` = `j`.`id_jenjang`
LEFT JOIN `tahun_ajaran` `ta` ON `u`.`id_tahun_ajaran` = `ta`.`id_tahun_ajaran`
INNER JOIN `guru_mapel_kelas_ajaran` `gmka_filter` ON `gmka_filter`.`mapel_id` = `u`.`mapel_id` AND `gmka_filter`.`kelas_id` = `sk`.`kelas_id` AND `gmka_filter`.`id_tahun_ajaran` = `u`.`id_tahun_ajaran`
WHERE `u`.`id_tahun_ajaran` = '1'
AND `gmka_filter`.`guru_id` = '2'
AND `h`.`nilai` = '100.00'
AND `h`.`status` = 'completed'
GROUP BY `s`.`id_siswa`, `s`.`nama`
